function parseJSON(data, isDebug = false) {
    try {
        if (data === '') {
            return {};
        }
        return JSON.parse(data);
    }
    catch (error) {
        console.error('Error parsing JSON:', error);
        if (isDebug) {
            console.error(new Error().stack);
        }
        return {};
    }
}

export { parseJSON };
